
use strict;

my $grid = [
   [qw(0 0 1 0 0)],
   [qw(0 2 3 4 0)],
   [qw(5 6 7 8 9)],
   [qw(0 A B C 0)],
   [qw(0 0 D 0 0)]

];
my $moves = {
    'L' => sub { L() },
    'R' => sub { R() },
    'U' => sub { U() },
    'D' => sub { D() },
};

my @instructions =  <DATA>;
chomp @instructions;

my $current = 5;

my $code = '';

for my $row (@instructions) {
    my @steps = split '', $row;
     for my $step(@steps) {
         $moves->{$step}->();
         print ":$current\n";
     }
    $code .= $current < 10 ? $current : [qw(A B C D)]->[$current - 10];
}

print "Code: $code\n";

sub in {
    my $keys = shift;
    for my $v(@$keys) {
        return 1 if $current == $v;
    }
    return 0;
}

sub L() {
  $current-- if in [3,4,6,7,8,9,11,12];
}

sub U() {
   if ($current == 3) {
       $current = 1;
   }
   elsif (in [6,7,8,10,11,12]) {
       $current -= 4;
   }
   elsif ($current == 13) {
       $current = 11;
   }
}

sub R() {
   $current ++ if in [2,3,5,6,7,8,10,11];
}

sub D() {
   if ($current == 1) {
       $current += 2;
   }
   elsif (in [2,3,4,6,7,8]) {
       $current += 4;
   }
   elsif ($current == 11) {
       $current = 13;
   }
}

